import 'package:flutter/material.dart';

void main() => runApp(MyApp());

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'NETFLIX',
      theme: ThemeData(
        appBarTheme: AppBarTheme(
          color: Colors.red,
          iconTheme: IconThemeData(color: Colors.black),
        ),
      ),
      home: Scaffold(
        appBar: AppBar(
          title: Text(
            'NETFLIX',
            style: TextStyle(
              color: Colors.black,
              fontFamily: 'halfback',
            ),
          ),
        ),
        body: Container(
          color: Colors.black,
          child: GridView.count(
            crossAxisCount: 3,
            childAspectRatio: 0.7,
            crossAxisSpacing: 10.0,
            mainAxisSpacing: 10.0,
            children: List.generate(6, (index) {
              return GridTile(
                child: FadeInImage(
                  placeholder: AssetImage('assets/placeholder.jpg.png'),
                  image: NetworkImage(_getPosterUrl(index)),
                  fit: BoxFit.cover,
                  fadeInDuration: Duration(milliseconds: 1700),
                  repeat: ImageRepeat.noRepeat,
                ),
                footer: Container(
                  color: Colors.black,
                  child: Center(
                    child: ElevatedButton(
                      onPressed: () {
                        print('Assistir ${index + 1}');
                      },
                      child: Row(
                        mainAxisAlignment: MainAxisAlignment.center,
                        children: [
                          Icon(Icons.play_arrow),
                          SizedBox(width: 5),
                          Text('Assistir'),
                        ],
                      ),
                      style: ButtonStyle(
                        backgroundColor:
                            MaterialStateProperty.all<Color>(Colors.red),
                      ),
                    ),
                  ),
                ),
              );
            }),
          ),
        ),
      ),
    );
  }

  String _getPosterUrl(int index) {
    final posters = [
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,',
    ];
    return posters[index % posters.length];
  }
}
